<?php

namespace Modules\Funcionario\Tests\Unit;

use Tests\TestCase;
use Illuminate\Foundation\Testing\WithFaker;
use Illuminate\Foundation\Testing\RefreshDatabase;

class FrequenciaTest extends TestCase
{
    /**
     * A basic unit test example.
     *
     * @return void
     */
    public function testSaveBiometryCorrect()
    {
        $response = $this->json('POST', '/api/funcionario/frequencia/biometry/1', ['biometria' => '']);

        TestCase::assertTrue(filter_var($response->getContent(), FILTER_VALIDATE_BOOLEAN));
    }

    public function testSaveBiometryIncorrect()
    {
        $response = $this->json('POST', '/api/funcionario/frequencia/biometry/1');

        TestCase::assertFalse(filter_var($response->getContent(), FILTER_VALIDATE_BOOLEAN));
    }
}
